import Resizer from "react-image-file-resizer";

import { AttachmentMeta } from "../common/types/AttachmentTypes";
import { RcFile } from "antd/es/upload";

/**
 * 
 */
export class Image_Util {
  public static image_src_fallback =
    "data:image/png;base64,";

  /**
   * Helper Function, to load an Image via Filereader
   * from the Filesysten, and return it as Base64 String
   *
   * @param {RcFile} file
   * @return {*}  {Promise<string>}
   */
  public static read_image_file_as_base64(file: RcFile): Promise<string> {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = (error) => reject(error);
    });
  }

  public static read_image_as_base64(blob: Blob): Promise<string> {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(blob);
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = (error) => reject(error);
    });
  }

  /**
   * 
   * @param file 
   * @returns 
   */
  public static read_image_file_and_resize(file: RcFile) {
    return new Promise((resolve) => {
      Resizer.imageFileResizer(
        file, // the image file object
        1000, // the new maxWidth
        1000, // the new maxHeight
        "JPEG", // the compression format (JPEG, PNG, WEBP)
        100, // the quality of the new image (0 to 100)
        0, // the rotation degree (0 to 360)
        (uri) => {
          // the callback function to get the URI
          resolve(uri);
        },
        "base64", // the desired output type (base64, blob, file.)
        1000, // the new minWidth
        1000 // the new minHeight
      );
    });
  }

  /**
   * Methode zum konvertieren von DataURL String zu bas64String.
   * 
   * @param dataURL 
   * @returns 
   */
  public static get_base64String_from_base64URL(dataURL: string) {
    return dataURL.replace("data:", "").replace(/^.+,/, "");
  }

  public static get_coverImage_src = (ama: AttachmentMeta[]): string => {
    let img_src: string = Image_Util.image_src_fallback;

    if (ama != null) {
      const result = ama.filter((am: AttachmentMeta) => {
        return am.category === "werk" && am.is_cover;
      });

      if (result.length > 0) {
        img_src = result[0].preview;
      }
    }

    return img_src;
  };
}